package javaexamples;
//1. Write a program in which there should not be any constructor and try to print values of variables for each data type

public class Contructors1 {
	 int a;
	 boolean b;
	 byte c;
	 short d;
	 long e;
	 char f;
	 float g;
	 double h;

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Contructors1 obj=new Contructors1();
		System.out.println(obj.a);
		System.out.println(obj.b);
		System.out.println(obj.c);
		System.out.println(obj.d);
		System.out.println(obj.e);
		System.out.println(obj.f);
		System.out.println(obj.g);
		System.out.println(obj.h);
		
	}

}
